mapper.select:
select

from
	user_ a



	a.user_name_ = #{userName}


	and a.real_name_ like '%${realName}%'


	a.dept_id_ = #{deptId}


	a.post_id_ = #{postId}


	a.lock_ = #{lock}


	and b.name_ = #{postName}


	and a.principal_ = #{principal}

select
from user_ a
where a.id_ = #{id}

mapper.insert:
insert into role_user_(
	role_id_,
	user_id_
) values(
	#{roleId},
	#{userId}
)
